import React, { useEffect } from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import Hidden from "@material-ui/core/Hidden";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardBody from "components/Card/CardBody.js";
import { connect } from "react-redux";
import { getAllCustomers } from "../../actions/customer";
import styles from "assets/jss/material-dashboard-react/views/iconsStyle.js";
import { cardHeader } from "assets/jss/material-dashboard-react";

const useStyles = makeStyles(styles);

function Icons(props) {
  let maxLostWeight = 0;
let nameOfWinner = "";

  useEffect(() => {
    props.getAllCustomers();
  }, [])
  const classes = useStyles();
  return (
    <GridContainer>
      <GridItem xs={12} sm={12} md={12}>
        <Card plain>
          <CardHeader plain color="primary">
            <h4 className={classes.cardTitleWhite}>המרתונים השבועיים שלנו</h4>
            <p className={classes.cardCategoryWhite}>
              herbalife  רד במשקל בדרך נכונה ובריאה וזכה בפרס מתנת {" "}
              {/* <a
                href="https://design.google.com/icons/?ref=creativetime"
                target="_blank"
              >
                Google
              </a> */}
            </p>
          </CardHeader>
          <CardBody>
            {/* <Hidden only={["sm", "xs"]}>
              <iframe
                className={classes.iframe}
                src="https://material.io/icons/"
                title="Icons iframe"
              >
                <p>Your browser does not support iframes.</p>
              </iframe>
            </Hidden> */}
            {/* <Hidden only={["lg", "md"]}>
              <GridItem xs={12} sm={12} md={6}>
                <h5>
                  The icons are visible on Desktop mode inside an iframe. Since
                  the iframe is not working on Mobile and Tablets please visit
                  the icons on their original page on Google. Check the
                  <a
                    href="https://design.google.com/icons/?ref=creativetime"
                    target="_blank"
                  >
                    Material Icons
                  </a>
                </h5>
              </GridItem>
            </Hidden> */}

            <div>
              <h3>:הזוכה השבועי שלנו</h3>
              {props.arr.forEach(element => {
                for (let i = element.customerWeights.length - 1, x = 0; x < 1; i-- , x++) {
                  if (element.customerWeights[i] - element.customerWeights[i - 1] > maxLostWeight) {
                    maxLostWeight = element.customerWeights[i] - element.customerWeights[i - 1];
                    nameOfWinner = element.firstName + " " + element.lastName;
                  }
                }
              })}
            </div>
            {nameOfWinner ? <p>nameOfWinner</p> : null}
          </CardBody>
        </Card>
      </GridItem>
    </GridContainer>
  );
}
const mapStateToProps = (state) => {
  return { arr: state.customerArr };
}
export default connect(mapStateToProps, { getAllCustomers })(Icons);
