import React, { useRef, useState, useEffect } from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
// core components
import GridItem from "components/Grid/GridItem.js";
import GridContainer from "components/Grid/GridContainer.js";
import CustomInput from "components/CustomInput/CustomInput.js";
import Button from "components/CustomButtons/Button.js";
import Card from "components/Card/Card.js";
import CardHeader from "components/Card/CardHeader.js";
import CardAvatar from "components/Card/CardAvatar.js";
import CardBody from "components/Card/CardBody.js";
import CardFooter from "components/Card/CardFooter.js";

import avatar from "assets/img/faces/marc.jpg";

// import React, { useRef, useState, useEffect } from 'react'
// import { Button, Form, Grid, Header, Icon, Message, Segment, Divider, Label } from 'semantic-ui-react'

const styles = {
  cardCategoryWhite: {
    color: "rgba(255,255,255,.62)",
    margin: "0",
    fontSize: "14px",
    marginTop: "0",
    marginBottom: "0",
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
  },
};

const useStyles = makeStyles(styles);
const Register1 = () => {

  return (<Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
    <Grid.Column style={{ maxWidth: 500 }}>
      <Header as='h1'>ברוך בואך לאתרינו</Header>
      <Header as='h1'>:לצורך זיהוי אנא הכנס את פרטייך</Header>
      <Message visible>שדות המסומנים ב-* הינם שדות חובה.</Message>
      <Form onSubmit={submit} >
        <Icon className="food"></Icon>
        <Form.Field>
          <input required ref={firstNameInput} onChange={() => {
            let x = firstNameInput.current.value;
            setFirstName(x);
          }}
            type='text' placeholder='שם פרטי' />
          {
            !firstNameValid ?
              <Label basic color='red' pointing>
                בבקשה הכנס שם פרטי תקין
                          </Label>
              :
              null
          }
        </Form.Field>
        <Divider />
        <Form.Field>
          <input required type='text' placeholder='שם משפחה' ref={lastNameInput} onChange={() => {
            let x = lastNameInput.current.value;
            setLastName(x);
          }} />
          {
            !lastNameValid ? <Label basic color='red' pointing>
              בבקשה הכנס שם משפחה תקין
              </Label> : null}

        </Form.Field>
        <Divider />
        <Form.Field>
          <input required type='text' placeholder='מספר פלאפון' ref={phoneInput}
            onChange={() => {
              let x = phoneInput.current.value;
              setPhone(x);
            }} />
          {
            !phoneValid ? <Label basic color='red' pointing>
              בבקשה הכנס מספר פלאפון
                  </Label>
              : null}
        </Form.Field>
        <Divider />
        <Form.Field inline>
          <input required type='text' placeholder='דוא"ל' />
          <Label basic color='red' pointing='left'>
            !  הכנס מייל תקין
                 </Label>
        </Form.Field>
        <Divider />
        <Form.Field inline>
          <Label basic color='red' pointing='right'>
            סיסמא חייבת להכיל 6 תווים לפחות
                  </Label>
          <input required type='password' placeholder='Password' />
        </Form.Field>
        <Header as='h4' color='teal' textAlign='right'>:כתובת</Header>
        <Form.Field>
          <input type='text' placeholder='עיר' />
          <Label basic color='red' pointing>
            בבקשה הכנס עיר
                  </Label>
        </Form.Field>
        <Form.Field>
          <input type='text' placeholder='רחוב' />
          <Label basic color='red' pointing>
            בבקשה הכנס רחוב
              </Label>
        </Form.Field>
        <Form.Field>
          <input type='text' placeholder='מספר רחוב' />
          <Label basic color='red' pointing>
            בבקשה הכנס מספר רחוב
              </Label>
        </Form.Field>
        <Button icon labelPosition='right'>
          הבא
              <Icon name='right arrow' />
        </Button>
      </Form>
      <Message>
        New to us? <a href='#'>Sign Up</a>
      </Message>
    </Grid.Column>
  </Grid >);
}



