import avatar from "assets/img/faces/marc.jpg";

// import React, { useRef, useState, useEffect } from 'react'
// import { Button, Form, Grid, Header, Icon, Message, Segment, Divider, Label } from 'semantic-ui-react'

const styles = {
  cardCategoryWhite: {
    color: "rgba(255,255,255,.62)",
    margin: "0",
    fontSize: "14px",
    marginTop: "0",
    marginBottom: "0",
  },
  cardTitleWhite: {
    color: "#FFFFFF",
    marginTop: "0px",
    minHeight: "auto",
    fontWeight: "300",
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif",
    marginBottom: "3px",
    textDecoration: "none",
  },
};

const useStyles = makeStyles(styles);
export default function UserProfile() {
  const classes = useStyles();
  let firstNameInput = useRef(null);
  const [firstNameValid, setFirstNameValid] = useState(true);
  const [firstName, setFirstName] = useState(true);
  let lastNameInput = useRef(null);
  const [lastNameValid, setLastNameValid] = useState(true);
  const [lastName, setLastName] = useState(true);
  let phoneInput = useRef(null);
  const [phoneValid, setPhoneValid] = useState(true);
  const [phone, setPhone] = useState(true);
  let mailInput = useRef(null);
  const [mailValid, setMailValid] = useState(true);
  const [mail, setMail] = useState(true);
  let passwordInput = useRef(null);
  const [passwordValid, setPasswordValid] = useState(true);
  const [password, setPassword] = useState("");
  let heightInput = useRef(null);
  const [heightValid, setHeightValid] = useState(true);
  const [height, setHeight] = useState("");
  let weightInput = useRef(null);
  const [weightValid, setWeightValid] = useState(true);
  const [weight, setWeight] = useState("");
  let ageInput = useRef(null);
  const [ageValid, setAgeValid] = useState(true);
  const [age, setAge] = useState("");
  useEffect(() => {
    lastName.length < 2 ? setLastNameValid(false) : setLastNameValid(true);
    firstName.length < 2 ? setFirstNameValid(false) : setFirstNameValid(true);
    phone.length < 10 || phone.length > 10 ? setPhoneValid(false) : setPhoneValid(true);
    password.length < 6 ? setPasswordValid(false) : setPasswordValid(true);
    // firstName.length < 2 ? setFirstNameValid(false) : setFirstNameValid(true);
  }, [firstName, firstNameValid, lastName, lastNameValid, phone, phoneValid,mail,mailValid,password,passwordValid,height,heightValid,weight,weightValid,age,ageValid])
  const submit = () => {
    if (firstNameValid && lastNameValid && phoneValid && mailValid && passwordValid&&heightValid&&weightValid&&ageValid) {

    }
  }
  return (
    <div>
      <GridContainer>
        <GridItem xs={12} sm={12} md={8}>
          <Card>
            <CardHeader color="primary">
              <h4 className={classes.cardTitleWhite}>הרשם לאתרינו</h4>
              <p className={classes.cardCategoryWhite}>אנא הכנס את פרטייך שדות שמסומנים בכוכבים הינם שדות חובה</p>
            </CardHeader>
            <CardBody>
              <h5 as='h7' color='teal' textAlign='right'>:פרטים אישיים</h5>

              <GridContainer>

                <GridItem xs={12} sm={12} md={4}>

                  <input required ref={firstNameInput} onChange={() => {
                    let x = firstNameInput.current.value;
                    setFirstName(x);
                  }}
                    type='text' placeholder='שם פרטי' />
                  {
                    !firstNameValid ?
                      //צריך ךחבר לפרויקט הזה את הסמנטיק כדי שהוא יהיה בעיצוב הרצוי
                      //לבינתיים הכנסתי לתוך דיב רגיל לא מעוצב כי הוא לא מכיר את הלייבל של הסמנטיק
                      <div>
                        בבקשה הכנס שם פרטי תקין
                            </div>
                      :
                      null
                  }
                  {/* <CustomInput
                    labelText="Company (disabled)"
                    id="company-disabled"
                    formControlProps={{
                      fullWidth: true,
                    }}
                    inputProps={{
                      disabled: true,
                    }}
                  /> */}
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                  <input required type='text' placeholder='שם משפחה' ref={lastNameInput} onChange={() => {
                    let x = lastNameInput.current.value;
                    setLastName(x);
                  }} />
                  {
                    !lastNameValid ?
                      <div>
                        בבקשה הכנס שם משפחה תקין
              </div>
                      : null}


                  {/* <CustomInput
                    labelText="Username"
                    id="username"
                    formControlProps={{
                      fullWidth: true,
                    }}
                  /> */}
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>

                  <input required type='text' placeholder='מספר פלאפון' ref={phoneInput}
                    onChange={() => {
                      let x = phoneInput.current.value;
                      setPhone(x);
                    }} />
                  {
                    !phoneValid ? <div>
                      בבקשה הכנס מספר פלאפון
                  </div>
                      : null}

                  {/* <CustomInput
                    labelText="Email address"
                    id="email-address"
                    formControlProps={{
                      fullWidth: true,
                    }} />*/}

                </GridItem>
              </GridContainer>
              <GridContainer>
                <GridItem xs={12} sm={12} md={6}>

                  <input required type='text' placeholder='דוא"ל' />
                  <div>
                    !  הכנס מייל תקין
                 </div>
                  {/* <CustomInput
                    labelText="First Name"
                    id="first-name"
                    formControlProps={{
                      fullWidth: true,
                    }}
                  /> */}
                </GridItem>
                <GridItem xs={12} sm={12} md={5}>
                  <input required type='password' placeholder='Password' />
                  <div>
                    סיסמא חייבת להכיל 6 תווים לפחות
                  </div>

                  {/* <CustomInput
                  required
                  type="password"
                    labelText="סיסמא"
                    id="password"
                    formControlProps={{
                      fullWidth: true,
                    }} /> */}

                </GridItem>
              </GridContainer>
              <h5 as='h7' color='teal' textAlign='right'>:נתונים</h5>

              <GridContainer>
                <GridItem xs={12} sm={12} md={4}>

                  <input ref={heightInput} onChange={() => {
                    let x = heightInput.current.value;
                    setHeight(x);
                  }}type='text' placeholder='גובה' />
                 {!heightValid ? <div>
                    בבקשה הכנס גובה
                  </div>:null}

                  {/* <CustomInput
                    labelText="City"
                    id="city"
                    formControlProps={{
                      fullWidth: true,
                    }}
                  /> */}
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                  <input ref={weightInput} onChange={() => {
                    let x = weightInput.current.value;
                    setWeight(x);
                  }}type='number' placeholder='משקל' />
                  {!weightValid ?<div>
                    בבקשה הכנס משקל
                </div>:null}
                  {/* <CustomInput
                    labelText="Country"
                    id="country"
                    formControlProps={{
                      fullWidth: true,
                    }}
                  /> */}
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                  <input ref={ageInput} onChange={() => {
                    let x = ageInput.current.value;
                    setAge(x);
                  }} type='number' placeholder='גיל' />
                  {!ageValid?<div>
                    בבקשה הכנס גיל
                  </div>:null}
                  {/* <CustomInput
                    labelText="Postal Code"
                    id="postal-code"
                    formControlProps={{
                      fullWidth: true,
                    }}
                  /> */}
                </GridItem>
              </GridContainer>
              <h5 as='h7' color='teal' textAlign='right'>:מין</h5>

              <GridContainer>
                <GridItem xs={12} sm={12} md={12}>
                  <form widths='equal'>
                    <form control='select'>
                      <option value='male'>זכר</option>
                      <option value='female'>נקבה</option>
                    </form>
                  </form>
                  {/* <CustomInput
                    labelText="Lamborghini Mercy, Your chick she so thirsty, I'm in that two seat Lambo."
                    id="about-me"
                    formControlProps={{
                      fullWidth: true,
                    }}
                    inputProps={{
                      multiline: true,
                      rows: 5,
                    }}
                  /> */}
                </GridItem>
              </GridContainer>
              <h5 as='h7' color='teal' textAlign='right'>:הקפים</h5>

              <GridContainer>
                <br />
                <GridItem xs={12} sm={12} md={4}>

                  <input ref={firstNameInput} onChange={() => {
                    let x = firstNameInput.current.value;
                    setFirstName(x);
                  }} type='text' placeholder='חזה' />
                  {!phoneValid ?<div>
                    בבקשה הכנס מספר
                  </div>:null}

                  {/* <CustomInput
                    labelText="City"
                    id="city"
                    formControlProps={{
                      fullWidth: true,
                    }}
                  /> */}
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                  <input ref={firstNameInput} onChange={() => {
                    let x = firstNameInput.current.value;
                    setFirstName(x);
                  }} type='number' placeholder='אגן' />
                 {!phoneValid ? <div>
                    בבקשה הכנס מספר
                </div>:null}
                  {/* <CustomInput
                    labelText="Country"
                    id="country"
                    formControlProps={{
                      fullWidth: true,
                    }}
                  /> */}
                </GridItem>
                <GridItem xs={12} sm={12} md={4}>
                  <input ref={firstNameInput} onChange={() => {
                    let x = firstNameInput.current.value;
                    setFirstName(x);
                  }} type='number' placeholder='מותן' />
                  {!phoneValid ?<div>
                    בבקשה הכנס מספר
                  </div>:null}
                  {/* <CustomInput
                    labelText="Postal Code"
                    id="postal-code"
                    formControlProps={{
                      fullWidth: true,
                    }}
                  /> */}
                </GridItem>
              </GridContainer>
              <h5 as='h7' color='teal' textAlign='right'>:אישור</h5>

              <GridContainer>
                <GridItem xs={12} sm={12} md={6}>

                  <input required ref={mailInput} onChange={() => {
                    let x = mailInput.current.value;
                    setMail(x);
                  }} type='text' placeholder='דוא"ל' />
                 {!mailValid ? <div>
                    !  הכנס מייל תקין
                 </div>:null}
                  {/* <CustomInput
                    labelText="First Name"
                    id="first-name"
                    formControlProps={{
                      fullWidth: true,
                    }}
                  /> */}
                </GridItem>
                <GridItem xs={12} sm={12} md={5}>
                  <input required ref={passwordInput} onChange={() => {
                    let x = passwordInput.current.value;
                    setPassword(x);
                  }} type='password' placeholder='Password' />
                  {!passwordValid ?<div>
                    סיסמא חייבת להכיל 6 תווים לפחות
                  </div>:null}

                  {/* <CustomInput
                  required
                  type="password"
                    labelText="סיסמא"
                    id="password"
                    formControlProps={{
                      fullWidth: true,
                    }} /> */}

                </GridItem>
              </GridContainer>

            </CardBody>
            <div>
              New to us? <a href='#'>Sign Up</a>
            </div>
            <CardFooter>
              <Button color="primary">עדכון פרופיל</Button>
            </CardFooter>
          </Card>
        </GridItem>
        <GridItem xs={12} sm={12} md={4}>
        </GridItem>
      </GridContainer>
    </div >
  );
}
